import React, { useState, useEffect } from 'react'
import Table from '../Table'
import { Users } from '../users'
import { AllSymptoms } from '../symptoms'

function Search({ query, setQuery, search }) {

  useEffect(() => {
  }, [])


  const handleCheckbox = (index) => {
    AllSymptoms.map((symptom) => {
      AllSymptoms[index].checkStatus = true;
    })
  }

  return (
    <div className='searchContainer'>
        <input type="text" placeholder='ძებნა...' className="search" onChange={e => setQuery(e.target.value)} />

        {AllSymptoms.map((symptom, index) => (
          <form action="" className='symtomCheckbox'>
            <input name="symptom" type="checkbox" onChange={() => handleCheckbox(index)}  />
            <label>{ symptom.symptom }</label>
          </form>
        ))}

        <Table data={search(Users)} />
    </div>
  )
}

export default Search
